  `handled_at` timestamp NULL DEFAULT NULL COMMENT '处理时间',
  `handle_action` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '处理动作',
